import 'package:QiYang/servers/common/widget/gesture_detector_widget.dart';import 'package:QiYang/servers/common/widget/tab/tab_selector_widget.dart';import 'package:QiYang/servers/common/widget/will_pop_scope_widget.dart';import 'package:QiYang/servers/home/bloc/home_banner_cubit.dart';import 'package:QiYang/servers/home/bloc/home_list_bloc.dart';import 'package:QiYang/servers/home/page/home_page.dart';import 'package:QiYang/servers/project/page/project_page.dart';import 'package:QiYang/util/log_utils.dart';import 'package:custom_navigation_bar/custom_navigation_bar.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:QiYang/servers/common/bloc/tab/tab_selector_bloc.dart';//导航管理class EntryDistributionPage extends StatefulWidget {  @override  _EntryDistributionPageState createState() => _EntryDistributionPageState();}class _EntryDistributionPageState extends State<EntryDistributionPage> {  @override  Widget build(BuildContext context) {    final state = (context.watch<TabSelectorBloc>().state as TabSelectorLoaded);    return WillPopScopeWidget(        child: Scaffold(            bottomNavigationBar: TabSelectorWidget(                items: state.tabItem, currentIndex: state.index),            resizeToAvoidBottomInset: false,            body: GestureDetectorWidget(              child: IndexedStack(                index: state.index,                children: [                  MultiBlocProvider(providers: [                    //banner                    BlocProvider(                        create: (BuildContext context) => HomeBannerCubit()),                    BlocProvider(                        create: (BuildContext context) => HomeListBloc()),                  ], child: HomePage()),                  ProjectPage(),                  ProjectPage(),                  ProjectPage(),                ],              ),            )));  }}